import Testimonios from "./Testimonios";

const App = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");
  const [isRegistering, setIsRegistering] = useState(false);
  const [userType, setUserType] = useState("");
  const [formData, setFormData] = useState({
    name: "",
    lastName: "",
    dni: "",
    address: "",
    city: "",
    dateOfBirth: "",
    password: "",
  });
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userEmail, setUserEmail] = useState("");
  const [userProfile, setUserProfile] = useState(null);
  const [currentPage, setCurrentPage] = useState("inicio");

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setMessage("");
    if (!userType) {
      setError("Por favor, selecciona un tipo de usuario.");
      return;
    }
    let apiUrl = "";
    if (userType === "Jugador") apiUrl = "http://localhost:8000/api/jugadores/login";
    else if (userType === "Club") apiUrl = "http://localhost:8000/api/clubs/login";
    else if (userType === "Admin") apiUrl = "http://127.0.0.1:8000/api/admins/login";

    try {
      const response = await fetch(apiUrl, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });
      const result = await response.json();
      if (!response.ok) throw new Error(result.error || "Error en el inicio de sesión");

      const loggedUser = {
        id: result.id,
        name: result.name,
        email: result.email,
        type: userType,
      };
      setUser(loggedUser);
      setUserEmail(result.email);
      setIsAuthenticated(true);
      setMessage("Sesión iniciada correctamente.");
      setEmail("");
      setPassword("");
      setUserType("");
      localStorage.setItem(
        "user",
        JSON.stringify({ ...loggedUser, tipo_usuario: userType })
      );
    } catch (err) {
      setError("Hubo un problema con el inicio de sesión. Inténtalo de nuevo.");
    }
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setUser(null);
    setUserEmail("");
    setMessage("Has cerrado sesión correctamente.");
    setUserProfile(null);
  };

  const handleRegisterSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setMessage("");
    if (!userType) {
      setError("Por favor, selecciona un tipo de usuario.");
      return;
    }
    let apiUrl = "";
    let payload = {};
    if (userType === "Jugador") {
      apiUrl = "http://127.0.0.1:8000/api/jugadores";
      payload = { ...formData, email, password };
    } else if (userType === "Club") {
      apiUrl = "http://127.0.0.1:8000/api/clubs";
      payload = { name: formData.name, email, city: formData.city, password };
    }

    try {
      const response = await fetch(apiUrl, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });
      const result = await response.json();
      if (!response.ok) throw new Error(result.message || "Error en el registro");

      setMessage("Registro exitoso. Ahora puedes iniciar sesión.");
      setEmail("");
      setPassword("");
      setUserType("");
      setFormData({ name: "", lastName: "", dni: "", address: "", city: "", dateOfBirth: "", password: "" });
      setIsRegistering(false);
    } catch (err) {
      setError("Hubo un problema con el registro. Inténtalo de nuevo.");
    }
  };

  return (
    <div className="container">
      <main className="main-content">
        <div className="centered-content">
          {isAuthenticated ? (
            <div className="contact-bar">
              <nav className="navigation">
                <ul>
                  {["inicio", "noticias", "ofertas", "testimonios", "soporte"].map((page) => (
                    <li key={page}>
                      <button onClick={() => setCurrentPage(page)}>{page.toUpperCase()}</button>
                    </li>
                  ))}
                  <li>
                    <button className="account-button" onClick={() => setCurrentPage("miCuenta")}>MI CUENTA</button>
                  </li>
                </ul>
              </nav>
            </div>
          ) : (
            <div className="contact-bar">
              <p>
                <a
                  href="https://mail.google.com/mail/?view=cm&fs=1&to=ffcvconnect@gmail.com"
                  className="custom-link"
                >
                  Contact Us: ffcvconnect@gmail.com
                </a>
              </p>
            </div>
          )}

          <div className="container-img">
            <img className="img1" src="/ffcvconnect.png" alt="FFCV Connect" />
          </div>

          {isAuthenticated ? (
            <div className="main-container">
              {currentPage === "inicio" && <Inicio />}
              {currentPage === "noticias" && <Noticias />}
              {currentPage === "testimonios" && <Testimonios />}
              {currentPage === "soporte" && <Soporte />}
              {currentPage === "miCuenta" && <MiCuenta user={user} userProfile={userProfile} handleLogout={handleLogout} />}
              {currentPage === "ofertas" && <Ofertas usuario={user} />}
              {currentPage === "quienesSomos" && <QuienesSomos />}
              {currentPage === "historia" && <Historia />}
            </div>
          ) : (
            <div className="form-container">
              <h2>{isRegistering ? "Registro" : "Iniciar Sesión"}</h2>
              {isRegistering ? (
                <form onSubmit={handleRegisterSubmit} className="form">
                  <div className="user-role-selection">
                    <label>Selecciona el tipo de usuario:</label>
                    <select
                      value={userType}
                      onChange={(e) => setUserType(e.target.value)}
                      className="role-dropdown"
                    >
                      <option value="">--Seleccionar--</option>
                      <option value="Jugador">Jugador</option>
                      <option value="Club">Club</option>
                    </select>
                  </div>
                  {userType === "Jugador" && (
                    <> /* campos Jugador */</>
                  )}
                  {userType === "Club" && (
                    <> /* campos Club */</>
                  )}
                  {/* Campos comunes de email y contraseña con visibilidad toggler */}
                  <button type="submit" className="submit-button">Registrarse</button>
                  <div className="toggle-link">
                    <p>
                      ¿Ya tienes cuenta?{' '}
                      <button type="button" className="link-button" onClick={() => setIsRegistering(false)}>
                        Inicia sesión aquí
                      </button>
                    </p>
                  </div>
                </form>
              ) : (
                <form onSubmit={handleLoginSubmit} className="form">
                  <div className="user-role-selection">
                    <label>Selecciona el tipo de usuario:</label>
                    <select
                      value={userType}
                      onChange={(e) => setUserType(e.target.value)}
                      className="role-dropdown"
                    >
                      <option value="">--Seleccionar--</option>
                      <option value="Jugador">Jugador</option>
                      <option value="Club">Club</option>
                      <option value="Admin">Admin</option>
                    </select>
                  </div>
                  {/* Campos email y contraseña con toggle */}
                  <button type="submit" className="submit-button">Iniciar sesión</button>
                  <div className="toggle-link">
                    <p>
                      ¿No tienes cuenta?{' '}
                      <button type="button" className="link-button" onClick={() => setIsRegistering(true)}>
                        Regístrate aquí
                      </button>
                    </p>
                  </div>
                </form>
              )}
              {error && <div className="error">{error}</div>}
              {message && <div className="message">{message}</div>}
            </div>
          )}
        </div>
      </main>

      <footer>
        <div className="footer-container">
          <div className="footer-column">
            <h3>Sobre nosotros</h3>
            <ul>
              <li>
                <button className="link-button" onClick={() => setCurrentPage("quienesSomos")}>Quiénes somos</button>
              </li>
              <li>
                <button className="link-button" onClick={() => setCurrentPage("historia")}>Historia</button>
              </li>
            </ul>
          </div>
          <div className="footer-column">
            <h3>Contacto</h3>
            <ul>
              <li>
                Email: <a href="mailto:ffcvconnect@gmail.com">ffcvconnect@gmail.com</a>
              </li>
              <li>Teléfono: +34 644 84 86 58</li>
              <li>Dirección: Calle Valencia-late, número 4, 12539 Les Alqueries, Castellón</li>
            </ul>
          </div>
          <div className="footer-column">
            <h3>Síguenos</h3>
            <ul>
              <li><a href="https://www.facebook.com/FFCV.es/?locale=ca_ES" target="_blank" rel="noopener noreferrer">Facebook</a></li>
              <li><a href="https://www.instagram.com/ffcv_info/" target="_blank" rel="noopener noreferrer">Instagram</a></li>
              <li><a href="https://x.com/FFCV_info" target="_blank" rel="noopener noreferrer">Twitter</a></li>
              <li><a href="https://www.youtube.com/@ffcv_info" target="_blank" rel="noopener noreferrer">YouTube</a></li>
            </ul>
          </div>
          <div className="footer-column footer-logo">
            <img src="https://ffcv.es/wp/wp-content/uploads/2019/09/logo_ffcv_fb.png" alt="FFCV Logo" />
          </div>
        </div>
        <p className="footer-bottom">Sitio desarrollado por FFCV | Todos los derechos reservados</p>
      </footer>
    </div>
  );
};

export default App;
